import React from 'react';
import { Container, Row, Col, img } from 'react-bootstrap';

const Login = () => {
  const [usuario, setUsuario] = useState('');
  const [contrasena, setContrasena] = useState('');

  return (
    <Container>
      <Row>
        <Col md={6}>
          <img src="/imagenes/lomito.png" alt="Lomito" />
        </Col>
        <Col md={6}>
          <form action="/api/login" method="post">
            <h1>INICIO DE SESIÓN</h1>
            <div className="mb-3">
              <label htmlFor="usuario"><strong>Usuario</strong></label>
              <input
                type="text"
                name="usuario"
                placeholder="Usuario"
                value={usuario}
                onChange={(e) => setUsuario(e.target.value)}
              />
              {usuario.length === 0 && <p className="text-danger">El campo usuario es obligatorio.</p>}
            </div>
            <div className="mb-3">
              <label htmlFor="contrasena"><strong>Contraseña</strong></label>
              <input
                type="password"
                name="contrasena"
                placeholder="Contraseña"
                value={contrasena}
                onChange={(e) => setContrasena(e.target.value)}
              />
              {contrasena.length === 0 && <p className="text-danger">El campo contraseña es obligatorio.</p>}
            </div>
            <button type="submit">Iniciar sesión</button>
            <button type="button" className="btn btn-secondary" onClick={() => setUsuario('') && setContrasena('')}>Cancelar</button>
          </form>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
