import React, { useState } from 'react';
import './Login.css';
import validacion from './LoginValidation.js';

function Login()  {
  const [values, setValues] = useState({
    usuario: '',
    password: ''
  })

const[errors, setErrors] = useState({})
const handleInput = (event) => {
  setValues(prev => ({...prev, [event.target.name]: [event.target.value]}))
}
const handleSubmit = (event) => {
    event.preventDefault();
    setErrors(validacion(values));
  }

  const handleError = (error, inputName) => {
    if (error) {
      return <span className='text-danger' style={{position: 'absolute', top: '20px', left: '5px'}}>{error}</span>;
    } else {
      return null;
    }
  };

return(
  <div className="login">
     <div className="login-imagen-container">
    <div className="login-imagen" />
    </div>
    <div className="login-formulario-container">
    <form action="" onSubmit={handleSubmit}>
      <div className="mb-3">
      <div className="Titulo-container">
        <h2 className="Titulo">INICIO DE SESIÓN</h2>
        <h3 className='Tipo-usuario'>Administrador</h3>
      </div>
        <label  htmlFor="usuario" className="Label-usuario" ><strong>Usuario</strong></label>
        <input type="text" placeholder="Usuario" name='usuario'
        onChange={handleInput} className="form-control rounded-0" />
        {handleError(errors.usuario, 'usuario')}
        <label htmlFor="password" className='Label-contrasena'><strong>Contraseña</strong></label>
        <input type="password" placeholder="Ingresa contraseña" name='password'
        onChange={handleInput} className="form-control rounded-0" />
        {handleError(errors.password, 'password')}
      </div>
      <div className='Boton-container'>
      <button type="submit" className="Boton">Iniciar sesión</button>
      </div>
    </form>
    </div>
    </div>
)
}

export default Login;
