  const handleSubmit = (e) => {
    e.preventDefault();

    // Valida los datos del formulario

    // Si los datos son válidos, envía los datos al servidor

    // ...
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6 d-flex justify-content-center">
          <img src="imagen.jpg" alt="Imagen de fondo" class="img-fluid" />
        </div>
        <div className="col-md-6">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="usuario"><strong>Usuario</strong></label>
              <input
                type="text"
                placeholder="Usuario"
                className="form-control rounded-0"
                value={usuario}
                onChange={(e) => setUsuario(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password"><strong>Contraseña</strong></label>
              <input
                type="password"
                placeholder="Ingresa contraseña"
                className="form-control rounded-0"
                value={contraseña}
                onChange={(e) => setContraseña(e.target.value)}
              />
            </div>
            <button className="btn btn-success">Iniciar sesión</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default App;
