function validacion(values){

let error = {}

if(values.usuario === ""){
    error.usuario = "El campo no debe estar vacio"
}
else{
    error.usuario = ""
}

if(values.password === ""){
    error.password = "El campo no debe estar vacio"
}
return error;
}

export default validacion;
