import React from 'react';

const Login = () => {
  const imagen = (
    <img src="/frontend/img/lomitoicon.png" alt="Logo" />
  );

  const formulario = (
    <form action="">
      <div className="mb-3">
        <label htmlFor="usuario"><strong>Usuario</strong></label>
        <input type="usuario" placeholder="Usuario" className="form-control rounded-0"/>
      </div>
      <div className="mb-3">
        <label htmlFor="password"><strong>Contraseña</strong></label>
        <input type="contrasena" placeholder="Ingresa contraseña" className="form-control rounded-0"/>
      </div>
      <button className="btn btn-success">Iniciar sesión</button>
    </form>
  );

  return (
    <div className="d-flex justify-content-center align-items-center bg-primary vh-100">
      {imagen}
      <div className="bg-white p-3 rounded w-25">
        {formulario}
      </div>
    </div>
  );
};

export default Login;
