import React, { useState } from 'react';
import { Container, Row, Col, img } from 'react-bootstrap';

const Login = () => {
  const [usuario, setUsuario] = useState('');
  const [contrasena, setContrasena] = useState('');

  return (
    <Container>
      <Row>
        <Col md={3}>
          <img src="/frontend/img/pantalla principal.png" alt="Lomito" />
        </Col>
        <Col md={9}>
          <form action="/api/login" method="post">
            <div className="mb-3">
              <label htmlFor="usuario"><strong>Usuario</strong></label>
              <input
                type="text"
                name="usuario"
                placeholder="Usuario"
                value={usuario}
                onChange={(e) => setUsuario(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="contrasena"><strong>Contraseña</strong></label>
              <input
                type="password"
                name="contrasena"
                placeholder="Contraseña"
                value={contrasena}
                onChange={(e) => setContrasena(e.target.value)}
              />
            </div>
            <button type="submit">Iniciar sesión</button>
          </form>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
